const testErc721Bytecode = ""

/**
 * Deploy ERC721Rarible contract (https://github.com/rarible/protocol-contracts/blob/master/tokens/contracts/erc-721/ERC721Rarible.sol)
 */
export async function deployTestErc721Ownable(web3: Web3, name: string = "test", symbol: string = "test") {
	const empty = createTestErc721(web3)
	const [address] = await web3.eth.getAccounts()
	return empty
		.deploy({ data: testErc721Bytecode, arguments: [name, symbol] })
		.send({ from: address, gas: 5000000, gasPrice: "0" })
}

export function erc721Mint(c: Contract, from: string, to: string, tokenId: string | number, uri: string = "") {
	return c.methods.mint(to, tokenId, uri).send({ from })
}

function createTestErc721(web3: Web3, address?: Address): Contract {
	return new web3.eth.Contract(testErc721Abi, address)
}